          display: block;
          margin: 3px 2px 3px 0;
        }

        .label-icon {
          display: block;
          margin-right: 6px;
        }

        :host(:focus) .contents.selected.focused .label-icon,
        :host(:focus) .contents.selected .label-icon {
          color: var(--vscode-list-activeSelectionForeground);
        }

        .multi .contents .label-icon {
          margin-top: 3px;
        }

        .label {
          display: block;
          line-height: 22px;
        }

